window.addEventListener('load', (e) => {
    const preloader = document.querySelector('.Pre-loder')
    preloader.classList.add('hide-Pre-loder')
  })
  
  
const scrollLinks = document.querySelectorAll(".scroll-link");
const navbar = document.querySelector('.header-nav')
const linksContainer = document.querySelector('.link-carryer')

scrollLinks.forEach((link) => {
    link.addEventListener("click", (e) => {
        e.preventDefault();
        const id = e.currentTarget.getAttribute("href").slice(1);
        const element = document.getElementById(id);

        const navHeight = navbar.getBoundingClientRect().height;
        const containerHeight = linksContainer.getBoundingClientRect().height;
        let position = element.offsetTop - navHeight;
        const stick_bar = navbar.classList.contains("stick-bar");
        if (!stick_bar) {
            position = position - navHeight;
        }
        if (navHeight > 82) {
            position = position + containerHeight;
        }
        window.scrollTo({
            left: 0,
            top: position,
        });

    });
});

const toggleBtn = document.querySelector(".toggle-bar");
const closeBtn = document.querySelector(".close-btn");
const sidebar = document.querySelector(".slider");

toggleBtn.addEventListener("click", function () {

  sidebar.classList.toggle("active-slider");
  const link = document.querySelectorAll('.slider-links li a')
  const x = link
  for (let i = 0; i < x.length; i++) {
    const element = x[i];

    element.addEventListener('click', (e) => {
      setTimeout(() => {
        sidebar.classList.remove("active-slider");
      }, 200);

    })

  }
});

closeBtn.addEventListener("click", function () {
  sidebar.classList.remove("active-slider");

});


